package com.edureka;

public class ArrayDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {2,4,6,8,10};
		for(int i=0;i<a.length;i++)
		{
			System.out.println(a[i]);
		}
		int[][] b= {
				{1,2},
				{3,4}
				};
		System.out.println(b[0][1]);

	}

}
